import { TodoItem } from "../models/todo.model";
import { useRecoilState } from "recoil";
import { todoListState, todoItemState } from "../states/todoStates";
import React, { useState } from "react";

const TodoList = () => {
  const [todoList, setTodoList] = useRecoilState(todoListState);
  const [todoItem, setTodoItem] = useRecoilState(todoItemState);

  const handleCheckboxOnChange = (id: string, isCompleted: boolean) => {
    setTodoList(
      todoList.map((todo) => {
        if (todo.id === id) {
          todo.isCompleted = !todo.isCompleted;
        }
        return todo;
      })
    );

    // const initialTodoList = [...todoList];

    // const todo = initialTodoList.find((a) => a.id === id);
    // const clone = { ...todo };
    // clone.isCompleted = isCompleted;

    // //todo!.isCompleted = true;
    // console.log(isCompleted);
    // console.log(initialTodoList);

    // setTodoList(initialTodoList);
    // const updatedTodoList = todoList.map((todo) => {
    //   if (todo.id === id) {
    //     todo.isCompleted = !todo.isCompleted;
    //   }

    //   return todo;
    // });

    //setTodoList(updatedTodoList);
  };

  const handleDeleteTodo = (id: string) => {
    const updatedTodoList = todoList.filter((todo) => todo.id !== id);
    setTodoList(updatedTodoList);
  };

  return (
    <ul>
      {todoList.map((todo) => (
        <div key={todo.id}>
          <li key={todo.id}>{todo.label}</li>
          <input
            type="checkbox"
            onChange={() => handleCheckboxOnChange(todo.id, !todo.isCompleted)}
            checked={todo.isCompleted}
          ></input>
          <button
            type="button"
            onClick={() => {
              handleDeleteTodo(todo.id);
            }}
          >
            Delete
          </button>
        </div>
      ))}
    </ul>
  );
};

export default TodoList;
