import "./App.css";
import { useState } from "react";
import uuid from "react-uuid";

function App() {
  const [todoInput, setTodoInput] = useState("");
  const [todoList, setTodoList] = useState([]);

  const handleOnChangeInput = (e) => {
    const { value } = e.target;
    setTodoInput(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const todo = {
      id: uuid(),
      label: todoInput,
      isCompleted: false,
    };

    setTodoList((prevState) => [...prevState, todo]);
    setTodoInput("");
    console.log(todo);
  };

  const handleCheckboxOnChange = (id) => {
    todoList.map((todo) => {
      if (todo.id === id) {
        todo.isCompleted = true;
      }

      return todo;
    });
  };

  return (
    <div className="App">
      <h1>Todo app</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          onChange={handleOnChangeInput}
          value={todoInput}
        ></input>
        <button type="submit">Add</button>
      </form>

      <ul>
        {todoList.map((todo) => (
          <div key={todo.id}>
            <li key={todo.id}>
              {todo.id} - {todo.label}
            </li>
            <input
              type="checkbox"
              onChange={() => handleCheckboxOnChange(todo.id)}
              checked={todo.isCompleted}
            ></input>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default App;
